function createfigure1(xvector1, ymatrix1)
%CREATEFIGURE1(XVECTOR1, YMATRIX1)
%  XVECTOR1:  bar xvector
%  YMATRIX1:  bar matrix data

%  Auto-generated by MATLAB on 23-Jun-2016 16:56:45

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1);
hold(axes1,'on');

% Create multiple lines using matrix input to bar
bar1 = bar(xvector1,ymatrix1);
set(bar1(4),'DisplayName','FF');
set(bar1(3),'DisplayName','SPSA');
set(bar1(2),'DisplayName','SA');
set(bar1(1),'DisplayName','ILP');

% Create xlabel
xlabel('Number of PODs');

% Create ylabel
ylabel('Established connections');

box(axes1,'on');
% Set the remaining axes properties
set(axes1,'FontSize',12,'XTick',[100 150 200],'YGrid','on');
% Create legend
legend1 = legend(axes1,'show');
set(legend1,'Location','northwest');

