function createfigure3(xvector1, ymatrix1,num)
%CREATEFIGURE3(XVECTOR1, YMATRIX1)
%  XVECTOR1:  bar xvector
%  YMATRIX1:  bar matrix data

%  Auto-generated by MATLAB on 23-Jun-2016 17:07:35

% Create figure
figure1 = figure;

% Create axes
axes1 = axes('Parent',figure1,...
    'Position',[0.137009345794392 0.157619047619048 0.774999999999999 0.815000000000001]);
hold(axes1,'on');

% Create multiple lines using matrix input to bar
bar1 = bar(xvector1,ymatrix1,'Parent',axes1);
set(bar1(4),'DisplayName','ILP');
set(bar1(3),'DisplayName','SA');
set(bar1(2),'DisplayName','SPSA');
set(bar1(1),'DisplayName','FF');

% Create xlabel
xlabel('Number of PODs','FontSize',13.2);

% Create ylabel
ylabel('Established connections','FontSize',13.2);

box(axes1,'on');
% Set the remaining axes properties
set(axes1,'FontSize',12,'XTick',[100 150 200],'YGrid','on');
% Create legend
legend1 = legend(axes1,'show');
set(legend1,'Location','northwest','FontSize',10.8);

% Create textbox
annotation(figure1,'textbox',...
    [0.499788524945192 0.0200342213846772 0.0261646452304584 0.0581308257254119],...
    'String',{num},...
    'LineStyle','none',...
    'FontSize',14,...
    'FitBoxToText','off');

